    public int countCharacters(String[] words, String chars) {
        if (words == null || words.length == 0) {
            return 0;
        }
        int[] letters = count(chars.toCharArray());
        int res = 0;
        for (String str : words) {
            int[] letter = count(str.toCharArray());
            if (isValid(letter, letters)) {
                res += str.length();
            }
        }
        return res;
    }

    public int[] count(char[] word) {
        int[] count = new int[26];
        for (int i = 0; i < word.length; i++) {
            count[word[i] - 'a']++;
        }
        return count;
    }

    public boolean isValid(int[] word, int[] chars) {
        for (int i = 0; i < 26; i++) {
            if (chars[i] < word[i]) {
                return false;
            }
        }
        return true;
    }

